def match_indexes(str1, str2):
    """Find indexes where two strings match (ignoring case)"""
    indexes = []
    for i in range(len(str1)):
        if str1[i].lower() == str2[i].lower():
            indexes.append(i)
    return indexes

def extract_binary(binary_str, indexes):
    """Extract binary digits at given indexes"""
    return ''.join([binary_str[i] for i in indexes])

def binary_to_string(binary_str):
    """Convert binary string to ASCII string"""
    return ''.join([chr(int(binary_str[i:i+8], 2)) for i in range(0, len(binary_str), 8)])

def main():
    # Example input strings
    binary_str = ""
    string1 = ""
    string2 = ""

    # Find matching indexes
    indexes = match_indexes(string1, string2)

    # Extract binary digits at matching indexes
    result_binary = extract_binary(binary_str, indexes)

    # Convert binary string to ASCII string
    result_string = binary_to_string(result_binary) 

    print("Original Binary String:", binary_str)
    print("Matching Indexes:", indexes)
    print("Result Binary String:", result_binary)
    print("Result String:", result_string)

if __name__ == "__main__":
    main()
